mapper.update:

update boards
   set   contents       =     #{contents} 
        ,notice         =     #{notice}
        ,title          =     #{title}
        ,updated_at     =       now()
where   bbs_id        =     #{bbs_id}
and     story_id        =     #{story_id}


update boards
   set      view       =     view    + 1 
 where   bbs_id        =     #{bbs_id}
 and     story_id        =     #{story_id}

mapper.delete:

delete from boards
 where   bbs_id        =     #{bbs_id}
   and   story_id      =     #{story_id}
